import React, {useRef, useEffect} from 'react';
import $ from 'jquery';
import './App.css';
import './style.css';
import { FiPhoneCall } from 'react-icons/fi';
import { FiInstagram } from 'react-icons/fi';
import { FiMail } from 'react-icons/fi';
import './fonts/GreatVibes-Regular.ttf';

const pieces = {
  black: [],
  white: []
};

const coord = (x, y, cW, cH) => {
  let grid = [];
  for (let i = 0; i <= cW; i += cW / 15){
    if (x < i){
      grid.push(i - cW / 15);
    }
  }
  for (let i = 0; i <= cH; i += cH / 15) {
    if (y < i){
      grid.push(i - cH / 15)
    }
  }
  if (grid.length == 2)
    return grid;
  else
    return undefined;
};

const Canvas = props => {
  
  const canvasRef = useRef(null)

  const getClickPos = (canvas, event) => {
    const cW = context.canvas.width * 0.97;
    const cH = context.canvas.height * 0.97;
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    pieces.black.push(coord(x, y, cW, cH))
  }

  const draw = ctx => {
    const p = ctx.canvas.height / 80;
    const cW = ctx.canvas.width * 0.97;
    const cH = ctx.canvas.height * 0.97;
    //set grid coord
    
    //draw grid 15x15
    for (let i = 0; i <= cW; i += cW / 15){
      ctx.moveTo(0.5 + i + p, p);
      ctx.lineTo(0.5 + i + p, cH + p)
    }
    for (let i = 0; i <= cH; i += cH / 15) {
      ctx.moveTo(p, 0.5 + i + p);
      ctx.lineTo(cW + p, 0.5 + i + p);
    }
    //use black lines for the grid
    ctx.strokeStyle = "black";
    ctx.stroke()

  }
  
  useEffect(() => {
    
    const canvas = canvasRef.current
    const context = canvas.getContext('2d')
    canvas.addEventListener('mousedown', (e) => {
      getClickPos(canvas, e)
    })
    //Our draw come here
    draw(context)
  }, [draw])
  
  return <canvas ref={canvasRef} {...props}/>
}

function App() {
  return (
    <div className="App">
        <link rel="stylesheet" type="text/css" href="style.css" />
      <head>
		    <title>
		    	Gomoku
	    	</title>
	    </head>
	    <body>
          {/* */}

          <Canvas width="300" height="300"></Canvas>

          {/* */}
          <script src="modal.js"></script>
	  </body>
  </div>
  );
}

console.clear();

console.log($(document).height());

export default App;
