import React, {useRef, useEffect} from 'react';
import $ from 'jquery';
import './App.css';
import './style.css';
import './canvas';
/* import { FiPhoneCall } from 'react-icons/fi';
import { FiInstagram } from 'react-icons/fi';
import { FiMail } from 'react-icons/fi'; */
import './fonts/GreatVibes-Regular.ttf';

/* stores clicks and whether they are valid piece placements
and also tracks whos turn it is */
const pieces = {
  black: [],
  white: [],
  turn: 'black'
};

/* 15x15 grid in memory */
const grid = [];
for (let i = 0; i < 15; i++){
  grid.push([])
  for (let j = 0; j < 15; j++){
    grid[i].push(`   `)
  }
}

function clearSelection(){
  if (window.getSelection)
    window.getSelection().removeAllRanges();
  else if (document.selection)
    document.selection.empty();
}

function App() {
  return (
    <div className="App">
        <link rel="stylesheet" type="text/css" href="style.css" />
      <head>
		    <title>
		    	Gomoku
	    	</title>
	    </head>
	    <body>
          {/* */}

          <Canvas width="495" height="495"></Canvas>
          <div id="whosturn">Black's turn to start</div>
          <button id="resign" onClick="resign()">RESIGN</button>
          <button id="draw" onClick="offerDraw()">OFFER DRAW</button>

          {/* */}
          <script src="modal.js"></script>
	  </body>
  </div>
  );
}

export default App;
